@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

/* Celebration animations */
@keyframes fall {
  0% {
    opacity: 1;
    transform: translateY(-100px) rotate(0deg);
  }
  50% {
    opacity: 1;
  }
  100% {
    opacity: 0;
    transform: translateY(100vh) rotate(360deg);
  }
}

@keyframes flash-blue {
  0%, 100% {
    color: hsl(var(--primary));
    text-shadow: 0 0 0px rgba(220, 38, 38, 0);
  }
  50% {
    color: hsl(0 78% 68%);
    text-shadow: 0 0 30px rgba(220, 38, 38, 0.8), 0 0 60px rgba(220, 38, 38, 0.5);
  }
}

/* Christmas Snowflake Animation */
@keyframes snowfall {
  0% {
    transform: translateY(-10vh) translateX(0px) rotate(0deg);
    opacity: 1;
  }
  100% {
    transform: translateY(100vh) translateX(100px) rotate(360deg);
    opacity: 0;
  }
}

@keyframes snowfall-2 {
  0% {
    transform: translateY(-10vh) translateX(0px) rotate(0deg);
    opacity: 1;
  }
  100% {
    transform: translateY(100vh) translateX(-100px) rotate(360deg);
    opacity: 0;
  }
}

/* Twinkling lights */
@keyframes twinkle {
  0%, 100% {
    opacity: 0.3;
  }
  50% {
    opacity: 1;
  }
}

@keyframes twinkle-delayed {
  0%, 100% {
    opacity: 0.2;
  }
  50% {
    opacity: 0.9;
  }
}

/* Christmas tree lights effect */
@keyframes light-pulse {
  0%, 100% {
    background-color: hsl(0 78% 48%);
    box-shadow: 0 0 10px hsl(0 78% 48% / 0.5);
  }
  25% {
    background-color: hsl(43 96% 56%);
    box-shadow: 0 0 10px hsl(43 96% 56% / 0.5);
  }
  50% {
    background-color: hsl(138 44% 32%);
    box-shadow: 0 0 10px hsl(138 44% 32% / 0.5);
  }
  75% {
    background-color: hsl(0 78% 48%);
    box-shadow: 0 0 10px hsl(0 78% 48% / 0.5);
  }
}

/* Tinsel shimmer */
@keyframes shimmer {
  0%, 100% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

.confetti-container {
  position: fixed;
  pointer-events: none;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 9999;
}

.confetti-piece {
  animation: fall 4s ease-in forwards;
  display: inline-block;
  position: fixed;
}

.celebration-score {
  animation: flash-blue 0.8s ease-in-out 7 !important;
}

@keyframes marquee {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(-50%);
  }
}

.animate-marquee {
  animation: marquee 20s linear infinite;
}

@keyframes gentle-float {
  0%, 100% {
    transform: translateY(0) rotate(0deg);
  }
  50% {
    transform: translateY(-10px) rotate(3deg);
  }
}

.animate-gentle-float {
  animation: gentle-float 4s ease-in-out infinite;
}

@keyframes sway {
  0%, 100% {
    transform: translateX(0px);
  }
  50% {
    transform: translateX(20px);
  }
}

.animate-sway {
  animation: sway 3s ease-in-out infinite;
}

@keyframes snowflake-fall {
  0% {
    transform: translateY(-100px) rotate(0deg);
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 0.8;
  }
  100% {
    transform: translateY(100vh) rotate(720deg);
    opacity: 0;
  }
}

.snowflake {
  position: fixed;
  color: white;
  text-shadow: 0 0 5px rgba(255, 255, 255, 0.5);
  animation: snowflake-fall linear infinite;
  pointer-events: none;
  z-index: 1;
}

.christmas-glow {
  box-shadow: 0 0 20px hsl(var(--primary) / 0.3), 0 0 40px hsl(var(--primary) / 0.1);
}

.card-festive {
  position: relative;
  overflow: hidden;
}

.card-festive::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 255, 255, 0.1),
    transparent
  );
  transition: left 0.5s ease;
}

.card-festive:hover::before {
  left: 100%;
}

@layer utilities {
  .text-gradient-christmas {
    background: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--accent)), hsl(var(--secondary)));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .safe-area-bottom {
    padding-bottom: env(safe-area-inset-bottom, 0px);
  }
}

client/src/lib/winProbability.ts.content:
import type { Game, Standings } from "@shared/schema";

/**
 * Calculate win probability for a team based on:
 * - Point Differential (standing indicator)
 * - Current score (during live games)
 * - Quarter progress (game momentum)
 */
export function calculateWinProbability(
  game: Game,
  team: "team1" | "team2",
  standings?: Standings[]
) {
  const standings1 = standings?.find(s => s.team === game.team1);
  const standings2 = standings?.find(s => s.team === game.team2);

  const team1PD = standings1?.pointDifferential || 0;
  const team2PD = standings2?.pointDifferential || 0;

  // Base probability from PD difference
  const pdDifference = team1PD - team2PD;
  let probability = 50 + (pdDifference / 20); // Each 20 points PD = 10% swing

  // Factor in score if game has started (quarter is not "Scheduled")
  if (game.quarter && game.quarter !== "Scheduled") {
    const scoreDifference = game.team1Score! - game.team2Score!;

    // Quarter weight multiplier - increases with game progress
    const quarterMap: { [key: string]: number } = {
      "1st": 0.3,
      "2nd": 0.5,
      "3rd": 0.75,
      "4th": 1.0,
      "Q1": 0.3,
      "Q2": 0.5,
      "Q3": 0.75,
      "Q4": 1.0,
    };

    const quarterWeight = quarterMap[game.quarter] || 0.5;

    // Score impact is more realistic: every 7 points = ~10% swing in later quarters
    const scoreImpact = (scoreDifference / 7) * 10 * quarterWeight;
    probability += scoreImpact;
  }

  // Cap between 1% and 99% (allow extreme differentials to show)
  probability = Math.max(1, Math.min(99, Math.round(probability)));

  // Return probability for the requested team
  if (team === "team1") {
    return probability;
  } else {
    return 100 - probability;
  }
}

